import React, {useState, useEffect} from 'react'
import './tradeblock.css'
import TBtrades from './TBtrades'
import TBoffers from './TBoffers'
import TBnfl from './TBnfl'
import TBnba from './TBnba'
import {Link} from 'react-router-dom'

const TradeBlock = (props) => {
    //props
    const user = props.user
    const nba = props.nba
    const nfl = props.nfl

    //states
    const [show, setShow] = useState('NBA')

    return(
        <div className="TradeBlock">
            <div className="tb-cash-div">simCash: ${user.cash && user.cash.toFixed(2)}</div>
            <div className="TBmenu scrollmenu scroll">
                <Link className={show === 'NBA' ? 'black selected' : 'black'} onClick={()=>{setShow('NBA')}}><span className={show === 'NBA' ? 'black selected' : 'black'}>simNBA</span></Link>
                <Link className={show === 'NFL' ? 'black selected' : 'black'} onClick={()=>{setShow('NFL')}}><span className={show === 'NFL' ? 'black selected' : 'black'}>simNFL</span></Link>
                <Link className={show === 'Trades' ? 'black selected' : 'black'} onClick={()=>{setShow('Trades')}}><span className={show === 'Trades' ? 'black selected' : 'black'}>Trades</span></Link>
                <Link className={show === 'Offers' ? 'black selected' : 'black'} onClick={()=>{setShow('Offers')}}><span className={show === 'Offers' ? 'black selected' : 'black'}>Offers</span></Link>
                <Link className={show === 'NBA' ? 'black selected' : 'black'} onClick={()=>{setShow('NBA')}}><span className={show === 'NBA' ? 'black selected' : 'black'}>simNBA</span></Link>
                <Link className={show === 'NFL' ? 'black selected' : 'black'} onClick={()=>{setShow('NFL')}}><span className={show === 'NFL' ? 'black selected' : 'black'}>simNFL</span></Link>
                <Link className={show === 'Trades' ? 'black selected' : 'black'} onClick={()=>{setShow('Trades')}}><span className={show === 'Trades' ? 'black selected' : 'black'}>Trades</span></Link>
                <Link className={show === 'Offers' ? 'black selected' : 'black'} onClick={()=>{setShow('Offers')}}><span className={show === 'Offers' ? 'black selected' : 'black'}>Offers</span></Link>
            </div>

            {show === 'NBA' && <TBnba user={user} nba={nba} />}
            {show === 'NFL' && <TBnfl user={user} nfl={nfl} />}
            {show === 'Trades' && <TBtrades />}
            {show === 'Offers' && <TBoffers />}

        </div>
    )
}

export default TradeBlock
