import React, {useState, useEffect} from 'react'
import './tradeblock.css'
import TBteamCard from './TBteamCard'


const TBnba = (props) => {
    //props
    const user = props.user
    
    //states
    const [nba, setNba] = useState(props.nba)
    const [sortType, setSortType] = useState()

    //Functionality
    const sortAtoZ = () => {
        let arr = nba
        arr.sort(function(a, b) {
            return a.team - b.team;
        })
        setSortType('AtoZ')
        return setNba(arr)
    }
    const sortZtoA = () => {
        let arr = nba
        arr.sort(function(a, b) {
            return b.lastSoldPrice - a.lastSoldPrice;
        })
        setSortType('ZtoA')
        return setNba(arr)
    }

    return(
        <div className="TBnba">
            {props.sort && <><button onClick={sortAtoZ}>A to Z</button><button onClick={sortZtoA}>Z to A</button></>}
            {nba && nba.map(t => {
                return <TBteamCard key={t.team} user={user} t={t} />
            })}
        </div>
    )
}

export default TBnba
