import React, {useState, useEffect} from 'react';
import {Line} from 'react-chartjs-2';
import './TeamCard.css'



const TeamGraph = (props) => {
    const [chartData, setChartData] = useState()

    const [isMobile, setMobile] = useState(window.innerWidth < 768);

    const prices = [150, 151, 153, 152, 154, 156, 153, 155, 158]
    const labels = ['01/01','01/02','01/03','01/04','01/05','01/06','01/07','01/08','01/09',]

    const updateMedia = () => {
      setMobile(window.innerWidth < 768);
    };
  
    useEffect(() => {
      window.addEventListener("resize", updateMedia);
      return () => window.removeEventListener("resize", updateMedia);
    });

    //Get the days into an array
    const getAllDays = (x) => {
      const allDays1 = []
      const allDaysName1 = []
      for(let i = x.tenure; i>0; i--){
          let day = moment().subtract(i, 'days');
          let date = (`0${day.date()}`).slice(-2)
          let month = (`0${day.month()+1}`).slice(-2)
          let year = day.year()
          allDays1.push(`${year}${month}${date}`)
          allDaysName1.push(`${month}/${date}`)
      }
      return allDays1, allDaysName1      
  }
  let allDays
  let allDaysName
  useEffect(()=>{
    let days = getAllDays()
    allDays = days.allDays1
    allDaysName = days.allDaysName1
  })

  const chart = () => {
    setChartData({
      labels: labels,
      datasets: [
        {
          fill: false,
          lineTension: 0.5,
          backgroundColor: '#c5b358',
          borderColor: '#c5b358',
          borderWidth: 3,
          data: prices,
          yAxisID: 'price1'
        },
      ]
    })
  }
  useEffect(() => {
    chart()
  }, [])

  return (
    <div>
      <Line
        data={chartData}
        options={{
          title:{
            display:false,
            text:' ',
            fontSize:20
          },
          legend:{
              display: false
          },
          layout: {
              padding: {
                  left: 20,
                  right: 20,
                  top: 20,
                  bottom: 20
              }
          },
          elements: {
              point: {
                  radius: 0
              }
          },
          scales:
          {
              yAxes: [{
                display : isMobile ? false : true,
                position: 'right',
                id: 'price1',
              }],
              xAxes: [{
                  display : false
              
          }]
          },
          //tooltips: {
          //    mode: 'nearest',
          //    intersect: false
          // },
          // hover: {
          //    mode: 'index',
          //    intersect: false
          //}
        }}
      />
    </div>
  )
}

export default TeamGraph
