import React, {useState, useEffect} from 'react'
import './tradeblock.css'
import TBtrades from './TBtrades'
import TBoffers from './TBoffers'
import TBnfl from './TBnfl'
import TBnba from './TBnba'
import {Link} from 'react-router-dom'
import {db} from '../../services/firebase'
import moment from 'moment'

const TradeBlock = (props) => {
    //props
    const user = props.user
    const nba = props.nba
    const nfl = props.nfl

    //states
    const [show, setShow] = useState('NBA')
    const [transactions, setTransactions] = useState([])
    const [sort, setSort] = useState(false)

    //Functions
    let date = moment().subtract(7,'d').format('YYYYMMDD')
    let todate = moment().format('YYYYMMDD')
    const getTransactions = () => { 
        let tranArr = []
        db.collection('Transactions').where('date', '>=', date).onSnapshot(querySnap => {
            querySnap.forEach(doc => {
                const data = doc.data()
                tranArr.push(data)
            })
            setTransactions(tranArr)
        })
    }
    useEffect(() => {
        getTransactions()
    }, [])

    return(
        <div className="TradeBlock">
            <div className="tb-cash-div">Cash: ${user.cash && user.cash.toFixed(2)}</div>
            <div className="TBmenu scrollmenu scroll">
                <Link className={show === 'NBA' ? 'black selected' : 'black'} onClick={()=>{setShow('NBA'); setSort(false)}}><span className={show === 'NBA' ? 'black selected' : 'black'}>simNBA</span></Link>
                <Link className={show === 'NFL' ? 'black selected' : 'black'} onClick={()=>{setShow('NFL'); setSort(false)}}><span className={show === 'NFL' ? 'black selected' : 'black'}>simNFL</span></Link>
                <Link className={show === 'Trades' ? 'black selected' : 'black'} onClick={()=>{setShow('Trades'); setSort(false)}}><span className={show === 'Trades' ? 'black selected' : 'black'}>Trades</span></Link>
                <Link className={show === 'Offers' ? 'black selected' : 'black'} onClick={()=>{setShow('Offers'); setSort(false)}}><span className={show === 'Offers' ? 'black selected' : 'black'}>Offers</span></Link>
                <Link onClick={()=>{setSort(!sort)}}><i className="fa fa-filter"></i></Link>
            </div>
            {show === 'NBA' && <TBnba user={user} nba={nba} sort={sort} />}
            {show === 'NFL' && <TBnfl user={user} nfl={nfl} sort={sort} />}
            {show === 'Trades' && <TBtrades transactions={transactions} week={date} today={todate} sort={sort} />}
            {show === 'Offers' && <TBoffers teams={props.teams} week={date} today={todate} sort={sort} />}

        </div>
    )
}

export default TradeBlock
