                        <Route exact path='/Privacy' component={Privacy} />
                        <Route exact path='/Tutorials'render={({history}) => (<Tutorial user={user} history={history} />)} />

                        {/* All */}
                        <Route path='/'render={() => (<Portfolio user={user} teams={teams} />)} />

                      </Switch> 
                  }
                </Switch>
              }
            </Switch>

        </ScrollToTop>
      </BrowserRouter>

    </div>
  )
}

