import React, {useState, useEffect} from 'react'
import './portfolio.css'
import {db} from '../../services/firebase'

const Bonus = (props) => {

    //props
    const user = props.user

    //states
    const [show, setShow] = useState(false)
    const [bonus, setBonus] = useState(0)

    //Functionality
    const getBonus = () => {
        db.collection('Affiliates').doc(user.code).get().then(doc => {
            if(doc.exists){
                const data = doc.data()
                setBonus(parseFloat(data.bonus))
            }
        })
    }
    useEffect(() => {
        if(user.code){
            getBonus()
        }
    }, [user.code])


    return( 
        <div className="Bonus">
            {!show && <button onClick={()=>{return setShow(true)}} className="bonus-btn">Collect Deposit Bonus</button>}
            {show && user.currentMoneyDeposited === 0 && <>
                <div className="collect-bonus-div">
                    Deposit to get a bonus!
                </div>
            </>}
            {show && user.currentMoneyDeposited !== 0 && bonus !==0 && <>
                <div className="collect-bonus-div">
                    <button className="collect-bonus-btn">Collect a Bonus of ${bonus.toFixed(2)}</button>
                </div>
            </>}
        </div>
    )
}

export default Bonus
