import React, {useState, useEffect} from 'react'
import './Holdings.css'
import {Link} from 'react-router-dom'
import { db } from '../../services/firebase'

const OpenAsks = (props) => {
    //props
    const user = props.user

    //states
    const [asks, setAsks] = useState()

    //Functionality
    const getAsks = () => {
        const asks1 = []
        db.collection('Asks').where('askOwner', '==', user.email).get().then(snapshot => {
            snapshot.forEach(doc => {
                const data = doc.data()
                asks1.push(data)
            })
            setAsks(asks1)
        })
    }
    useEffect(() => {
        getAsks()
    }, [])

    return(
        <div className="OpenAsks">
            <div className="holdings-offers-div"><Link>Open Offers to Sell</Link></div>
        </div>
    )
}

export default OpenAsks
