import React, {useState, useEffect} from 'react'
import './portfolio.css'
import PPayouts from './PPayouts'

const PTotal = (props) => {
    //get props
    const user = props.user
    const teams = props.teams

    //get states
    const [value, setValue] = useState(0)
    const [percentReturn, setPercentReturn] = useState(0)

    //Functions
    const getValue = () => {
        if(user.cash){
            let holder = 0
                user.shares && user.shares.map(s => {
                    let team = s.substring(0, s.length - 4)
                    teams !== undefined && teams.map(t => {
                        if(team === t.team){
                            holder = holder + t.lastSoldPrice
                        }
                    })
                })
            return (holder + user.cash).toFixed(2)
        }
        return "-"
    }

    const getPercentReturn = () => {
        return ((((getValue() - user.currentMoneyDeposited) / user.currentMoneyDeposited) * 100).toFixed(2))
    }


    return(
        <div className='PTotal'>
            <div className="p-total-account">Account Value</div>
            <hr className="p-total-hr" />
            <div className="p-total-value">${getValue()}</div>
            <div className="p-total-cash">Cash: <span className="p-total-cash-amt">${user.cash && user.cash.toFixed(2)}</span></div>
            <PPayouts user={user} teams={teams} />
            <hr className="p-graph-top" />
            <div className={getPercentReturn() >=0 ? "p-graph-return green" : "p-graph-return red" }>${(getValue() - user.currentMoneyDeposited).toFixed(2)} [{getPercentReturn()>=0 && '+'}{getPercentReturn()}%] - All</div>
        </div>
    )
}

export default PTotal
