import React, {useState} from 'react'
import './tradeblock.css'
import TBmenu from './TBmenu'
import TBtrades from './TBtrades'
import TBoffers from './TBoffers'
import TBnfl from './TBnfl'
import TBnba from './TBnba'
import {Link} from 'react-router-dom'

const TradeBlock = () => {

    const [show, setShow] = useState('Trades')

    return(
        <div className="TradeBlock">
            <div className="TBmenu scrollmenu scroll">
                <Link className="black" onClick={()=>{setShow('Trades')}}><span>Trades</span></Link>
                <Link className="black" onClick={()=>{setShow('Offers')}}><span>Offers</span></Link>
                <Link className="black" onClick={()=>{setShow('NFL')}}><span>simNFL</span></Link>
                <Link className="black" onClick={()=>{setShow('NBA')}}><span>simNBA</span></Link>
                <Link className="black" onClick={()=>{setShow('Trades')}}><span>Trades</span></Link>
                <Link className="black" onClick={()=>{setShow('Offers')}}><span>Offers</span></Link>
                <Link className="black" onClick={()=>{setShow('NFL')}}><span>simNFL</span></Link>
                <Link className="black" onClick={()=>{setShow('NBA')}}><span>simNBA</span></Link>
            </div>

            {show === 'Trades' && <TBtrades />}
            {show === 'Offers' && <TBoffers />}
            {show === 'NFL' && <TBnfl />}
            {show === 'NBA' && <TBnba />}

        </div>
    )
}

export default TradeBlock
