import React, {useState, useEffect} from 'react'
import './Holdings.css'
import {Link} from 'react-router-dom'
import {db} from '../../services/firebase'

const OpenBids = (props) => {
    //props
    const user = props.user

    //states
    const [bids, setBids] = useState([])
    const [show, setShow] = useState(false)

    //Funcitonality
    const getBids = () => {
        if(show){
            const bids1 = []
            db.collection('Bids').where('bidOwner', '==', user.email).get().then(snapshot => {
                snapshot.forEach(doc => {
                    const data = doc.data()
                    bids1.push(data)
                })
                setBids(bids1)
            })
        }
    }
    useEffect(() => {
        getBids()
    }, [show])

    return(
        <div className="OpenBids">
            <div className="holdings-offers-div"><Link onClick={()=>{setShow(!show)}}>Open Offers to Buy</Link></div>
        </div>
    )
}

export default OpenBids
