import React, {useState, useEffect} from 'react'
import './tradeblock.css'
import TBtradeCard from './TBtradeCard'

const TBtrades = (props) => {

    //states
    const [pastDay, setPastDay] = useState()
    const [pastWeek, setPastWeek] = useState()

    //Functions
    const countTransactions = () => {
        let day = 0
        let week = 0
        if(props.transactions){
            props.transactions.map(t => {
                if(t.date >= props.week){
                    week = week + 1
                }
                if(t.date >= props.today){
                    day = day + 1
                }
            })
            setPastDay(day)
            setPastWeek(week)
        }
    }
    useEffect(() => {
        countTransactions()
    }, [props])

    return(
        <div className="TBtrades">
            <div className="tb-t-div">
                <div className="tb-t-title">Trades</div>
                <div className="tb-t-day">1 Day</div>
                <div className="tb-t-day">7 Days</div>
                <hr className="tb-t-hr" />
                <hr className="tb-t-hr" />
                <div className="tb-t-amt">{pastDay}</div>
                <div className="tb-t-amt">{pastWeek}</div>
            </div>
            <div className="tb-cards">
                <TBtradeCard />
                {props.transactions.length > 1 && props.transactions.map(t => {
                    return <TBtradeCard t={t} />
                })}
            </div>
            
        </div>
    )
}

export default TBtrades
