import React, {useState, useEffect} from 'react'
import './tradeblock.css'
import TBteamCard from './TBteamCard'


const TBnba = (props) => {
    //props
    const user = props.user

    //states
    const [nba, setNba] = useState(props.nba)

    //Functionality
    const sortAtoZ = () => {
        let nbaArr = nba
        new Promise
        const myPromise = new Promise( (res, rej) => {
            nbaArr.sort(function(a, b) {
                return b.lastSoldPrice - a.lastSoldPrice;
            })
        })
        myPromise.then(res => setNba(nbaArr))
    }

    return(
        <div className="TBnba">
            {props.sort && <button onClick={sortAtoZ}>A to Z</button>}
            {nba.map(t => {
                return <TBteamCard key={t.team} user={user} t={t} />
            })}
        </div>
    )
}

export default TBnba
