import React, {useState, useEffect} from 'react'
import './tradeblock.css'
import TBteamCard from './TBteamCard'


const TBnba = (props) => {
    //props
    const user = props.user
    
    //states
    const [nba, setNba] = useState(props.nba)
    const [sortType, setSortType] = useState()

    //Functionality
    const sortLow2High = () => {
        let arr = nba
        arr.sort(function(a, b) {
            return a.lastSoldPrice - b.lastSoldPrice;
        })

        return setNba(arr)
    }
    const sortHigh2Low = () => {
        let arr = nba
        arr.sort(function(a, b) {
            return b.lastSoldPrice - a.lastSoldPrice;
        })
        setSortType('ZtoA')
        return setNba(arr)
    }
    const sortLow2HighAsk = () => {
        let arr = nba
        arr.sort(function(a, b) {
            return a.lowestAskPrice - b.lowestAskPrice;
        })
        setSortType('AtoZ')
        return setNba(arr)
    }
    const sortHigh2LowAsk = () => {
        let arr = nba
        arr.sort(function(a, b) {
            return b.lowestAskPrice - a.lowestAskPrice;
        })
        setSortType('ZtoA')
        return setNba(arr)
    }
    const sortLow2HighBid = () => {
        let arr = nba
        arr.sort(function(a, b) {
            return a.highestBidPrice - b.highestBidPrice;
        })
        setSortType('AtoZ')
        return setNba(arr)
    }
    const sortHigh2LowBid = () => {
        let arr = nba
        arr.sort(function(a, b) {
            return b.highestBidPrice - a.highestBidPrice;
        })
        setSortType('ZtoA')
        return setNba(arr)
    }


    return(
        <div className="TBnba">
            {props.sort && <>
                <div>
                    Price: <button onClick={sortLow2High}>Low to High</button>
                    <button onClick={sortHigh2Low}>High to Low</button>
                </div>
                <div>
                    Bid: <button onClick={sortLow2HighBid}>Low to High</button>
                    <button onClick={sortHigh2LowBid}>High to Low</button>
                </div>
                <div>
                    Ask: <button onClick={sortLow2HighAsk}>Low to High</button>
                    <button onClick={sortHigh2LowAsk}>High to Low</button>
                </div>
                
            </>}
            {nba && nba.map(t => {
                return <TBteamCard key={t.team} user={user} t={t} />
            })}
        </div>
    )
}

export default TBnba
