import React, { useState } from "react";
//import '../Acount.css'
import firebase from 'firebase'
import {db} from '../../../services/firebase'
import moment from 'moment'

export default function ReactPayPal(props) {
  const user = props.user
  const [paid, setPaid] = React.useState(false);
  const [error, setError] = React.useState(null);
  const [minAmt, setMinAmt] = useState()
  const paypalRef = React.useRef();
  const [dbDeposit, setdbDeposit] = useState(false)

  let depositValue 

  // To show PayPal buttons once the component loads
  React.useEffect(() => {

      window.paypal
      .Buttons({
        createOrder: (data, actions) => {
            return actions.order.create({
              intent: "CAPTURE",
              purchase_units: [
                {
                  description: `SimBull Cash for user: ${user.email}`,
                  amount: {
                    currency_code: "USD",
                    value: props.amount
                  }
                }
              ]
            });
        },
        onApprove: async (data, actions) => {
          const order = await actions.order.capture();
          setPaid(true);
          console.log(order);
        },
        onError: (err) => {
          //   setError(err),
          console.error(err);
        }
      })
      .render(paypalRef.current);
    
    
  }, []);

  // If the payment has been made
  if (paid) {
    console.log(1)
    let date = moment().format('MMMM Do YYYY, h:mm:ss a')
    if(!dbDeposit){
      console.log(2)
      setdbDeposit(true)
      let amount = parseFloat(props.amount)
      let pBonus = 0
      let dBonus = 0
      //random twitter guy
      if(user.code && user.code === 324923){pBonus = 5}
      //shooting sports
      if(user.code && user.code === 879312){pBonus = 5}
      //mlm
      if(user.code && user.code === 649184){pBonus = 5}
      //utah jazz
      if(user.code && user.code === 291384){pBonus = 5}
      //dallas mavericks
      if(user.code && user.code === 238976){pBonus = 5}
      //76ers (browns)
      if(user.code && user.code === 938784){pBonus = 5}
      //trailblazers (saints)
      if(user.code && user.code === 162053){pBonus = 5}
      //james klein ref
      if(user.code && user.code === 825792){pBonus = 0}
      pBonus = 0
      if(user.code === 'NBAZayy' || user.code === 'hoopology' || user.code === 'grantsportz' || user.code === 'PigSpin'){if(user.currentMoneyDeposited === 0){pBonus = 10}}
      if(amount >= 100){
        dBonus = 0 * amount
        if(amount >=500){
          dBonus = 0 * amount
        }
        if(dBonus > 200){
          dBonus = 200
        }
        console.log(3)
        db.collection('Deposits').doc(`${date}-${user.email}`).set({
          user: user.email,
          amount: amount,
          date: date,
          bonus: pBonus + dBonus,
          type: 'deposit',
          userCode: user.code && user.code
        })
        db.collection('Users').doc(user.email).update({
          cash: firebase.firestore.FieldValue.increment(amount + pBonus + dBonus),
          currentMoneyDeposited: firebase.firestore.FieldValue.increment(amount),
          totalValue: firebase.firestore.FieldValue.increment(amount + pBonus + dBonus),
          bonusesReceived: pBonus + dBonus
        }).then((result) => {
          alert(`Deposit Successful! The funds will appear in the portfolio after the page is refreshed.`)
          return window.location.href = "https://simbull.app";
        })
      }else{ 
          db.collection('Deposits').doc(`${date}-${user.email}`).set({
            user: user.email,
            amount: amount,
            date: date,
            bonus: pBonus,
            type: 'deposit'
          })
          db.collection('Users').doc(user.email).update({
            cash: firebase.firestore.FieldValue.increment(amount + pBonus),
            currentMoneyDeposited: firebase.firestore.FieldValue.increment(amount + pBonus),
            totalValue: firebase.firestore.FieldValue.increment(amount + pBonus),
          }).then((result) => {
            alert(`Deposit Successful! The funds will appear in the portfolio after the page is refreshed.`)
            return window.location.href = "https://simbull.app";
          })
        }  
      
    }
    return <div>Deposit Successful! The funds will appear in the portfolio after the page is refreshed.</div>;
  }

  // If any error occurs
  if (error) {
    return <div>Error Occurred in processing payment! Please try again.</div>;
  }

  // Default Render
  return (
    <div className="Transfer">
                    <div className="Paypal-Box">
                        <br></br>
                        <br></br>
                        <p className="Paypal-amount"><br></br>${props.amount}</p>
                        <p>Click the Payment Option You Prefer</p>
                        <div ref={paypalRef} />
                    </div>
    </div>
  );
}
