import React, {useState} from 'react'
import './functions.css'
import '../Holdings/TeamCard.css'
import BuyShare from './BuyShare'
import SellShare from './SellShare'
import CreateAsk from './CreateAsk'
import CreateBid from './CreateBid'

const TradeButtons = (props) => {
    //props
    const shares = props.shares
    const team = props.team
    const user = props.user

    //states
    const [showBuy, setShowBuy] = useState(false)
    const [showSell, setShowSell] = useState(false)
    const [showAsk, setShowAsk] = useState(false)
    const [showBid, setShowBid] = useState(false)
    const [showOffers, setShowOffers] = useState(false)

    //Functionality
    const closeSell = () => {return setShowSell(false)}
    const closeBuy = () => {return setShowBuy(false)}

    const routeOffers = () => {
        if(shares === 0){
            showBid(true)
        }else{
            showOffers(true)
        }
    }

    return(
        <div className="TradeButtons">
            <div className="te-function-div">
                <button onClick={()=>{setShowBuy(true)}} className="te-function-btn">Buy</button>
                <button className="te-function-btn">Offer</button>
                <button onClick={()=>{setShowSell(true)}} className="te-function-btn">Sell</button>
            </div>
            {showBuy && <BuyShare team={team} user={user} closeBuy={closeBuy} />}
            {showSell && <SellShare shares={shares} team={team} user={user} closeSell={closeSell} />}
            {showAsk && <CreateAsk />}
            {showBid && <CreateBid />}
        </div>
    )
}

export default TradeButtons
