import React, {useState, useEffect} from 'react'
import './Analysis.css'
import {db, firebaseAnalytics} from '../../services/firebase'

const Analysis = (props) => {
    //props
    const user = props.user

    //states
    const [content, setContent] = useState([])

    

    const media = [
        {
            title: `Most Undervalued NBA Teams`,
        },
        {
            title: 'NFL Teams to Target this Offseason',
        },
        {
            title: 'Best Long Term Plays',
        },
        {
            title: 'Are the Bucks undervalued?',
        },
        {
            title: 'NBA Power Rankings',
        },
        {
            title: 'Deep Dive into the NFC West',
        },
        {
            title: 'Who is the must undervalued team right now?'
        },
        {
            title: 'Should you sell NFL teams and buy NBA?'
        },
        {
            title: 'How the NFL Draft could affect share prices'
        },
        {
            title: 'Most Undervalued NBA Teams',
        },
        {
            title: 'NFL Teams to Target this Offseason',
        },
        {
            title: 'Best Long Term Plays',
        },
        {
            title: 'Are the Bucks undervalued?',
        },
        {
            title: 'NBA Power Rankings',
        },
        {
            title: 'Deep Dive into the NFC West',
        },
        {
            title: 'Who is the must undervalued team right now?'
        },
        {
            title: 'Should you sell NFL teams and buy NBA?'
        },
        {
            title: 'How the NFL Draft could affect share prices'
        },
    ]

    //Functionality
    const getContent = () => {
        const content1 = []
        db.collection('Analysis').get().then(snapshot => {
            snapshot.forEach(doc => {
                const data = doc.data()
                content1.push(data)
            })
            setContent(content1)
        })
    }
    useEffect(() => {
        getContent()
    }, [])


    useEffect(()=>{
        firebaseAnalytics.logEvent("analysis_visit", {
           user: user && user.email 
        })
    },[])


    return(
        <div className="Analysis">
            {content.length > 0 && content.map(c => {
                console.log(c)
                return(
                    <div className="anal-card">
                        <a href={c.link} target="_blank" className="blk-text">
                            <hr />
                            <div className="anal-title">{c.title}</div>
                            <div className="anal-sub">{c.type} - By: {c.author} - {`${c.date.substring(4, 6)}/${c.date.substring(6, 8)}/${c.date.substring(0, 4)}`}</div>
                         </a>
                    </div>
                )
            })}
            {media.map(m => {
                return(
                        <div className="anal-card"> 
                            <hr />
                            <div className="anal-title">{m.title}</div>
                            <div className="anal-sub">Klein Media - By Dallas Klein - Jan 06, 2021</div>
                        </div>
                )
            })}
        </div>
    )
}

export default Analysis
