import React, {useState, useEffect} from 'react'
import './Holdings.css'
import {Link} from 'react-router-dom'
import {db} from '../../services/firebase'

const OpenBids = (props) => {
    //props
    const user = props.user

    //states
    const [bids, setBids] = useState([])
    const [bidPrices, setBidPrices] = useState([])
    const [bidAmt, setBidAmt] = useState({})
    const [show, setShow] = useState(false)

    //Funcitonality
    const getBids = () => {
        if(show){
            const bids1 = []
            let bidArr = []
            let bidAmtObj = {}
            db.collection('Bids').where('bidOwner', '==', user.email).get().then(snapshot => {
                snapshot.forEach(doc => {
                    const data = doc.data()
                    bids1.push(data)
                    if(bidArr.includes(data.bidPrice)){
                        bidAmtObj[data.bidPrice]++
                    }
                    else{
                        bidArr.push(data.bidPrice)
                        bidAmtObj[data.bidPrice] = 1
                    }
                    
                })
                bidArr.sort(function(a, b) {
                    return b - a;
                });
                setBids(bids1)
                setBidPrices(bidArr)
                setBidAmt(bidAmtObj)
            })
        }
    }
    useEffect(() => {
        getBids()
    }, [show])

    return(
        <div className="OpenBids">
            <div className="holdings-offers-div"><Link onClick={()=>{setShow(!show)}}>{show ? 'Close' : 'Open'} Offers to Buy</Link></div>
        </div>
    )
}

export default OpenBids
