import React, {useState, useEffect} from 'react'
import './tradeblock.css'
import TBofferCard from './TBofferCard'
import moment from 'moment'
import {db} from '../../services/firebase'

const TBoffers = () => {

    //states
    const [bids, setBids] = useState([])
    const [asks, setAsks] = useState([])
    const [offers, setOffers] = useState([])

    //Functions
    let date = moment().subtract(7,'d').format('YYYYMMDD')
    let todate = moment().format('YYYYMMDD')
    const getOffers = () => { 
        let bidArr = []
        let askArr = []
        db.collection('Bids').where('date', '>=', date).onSnapshot(querySnap => {
            querySnap.forEach(doc => {
                const data = doc.data()
                bidArr.push(data)
            })
            setBids(bidArr)
        })
        db.collection('Asks').where('date', '>=', date).onSnapshot(querySnap => {
            querySnap.forEach(doc => {
                const data = doc.data()
                askArr.push(data)
            })
            setAsks(askArr)
        })
    }
    useEffect(() => {
        getOffers()
    }, [])
    const combineOffers = () => {
        let offersArr = []
        console.log(asks)
        console.log(bids)
    }
    useEffect(() => {
        combineOffers()
    }, [bids, asks])
 
    return(
        <div className="TBoffers">
                <div className="tb-o-div-left">
                <div className="tb-t-title">Bids</div>
                <div className="tb-t-day">1 Day</div>
                <div className="tb-t-day">7 Days</div>
                <hr className="tb-t-hr" />
                <hr className="tb-t-hr" />
                <div className="tb-t-amt">41</div>
                <div className="tb-t-amt">213</div>
            </div>
            <div className="tb-o-div-right">
                <div className="tb-t-title">Asks</div>
                <div className="tb-t-day">1 Day</div>
                <div className="tb-t-day">7 Days</div>
                <hr className="tb-t-hr" />
                <hr className="tb-t-hr" />
                <div className="tb-t-amt">41</div>
                <div className="tb-t-amt">213</div>
            </div>
            <div className='tb-cards'>
                <TBofferCard />
                <TBofferCard />
                <TBofferCard />
                <TBofferCard />
                <TBofferCard />
                <TBofferCard />
                <TBofferCard />
                <TBofferCard />
                <TBofferCard />
                <TBofferCard />
                <TBofferCard />
            </div>
        </div>
    )
}

export default TBoffers
