import React, {useState, useEffect} from 'react'
import './Holdings.css'
import {Link} from 'react-router-dom'
import { db } from '../../services/firebase'

const OpenAsks = (props) => {
    //props
    const user = props.user

    //states
    const [asks, setAsks] = useState()
    const [askPrices, setAskPrices] = useState([])
    const [askAmt, setAskAmt] = useState({})
    const [show, setShow] = useState(false)

    //Functionality
    const getAsks = () => {
        if(show){
            const asks1 = []
            let askArr = []
            let askAmtObj = {}
            db.collection('Asks').where('askOwner', '==', user.email).get().then(snapshot => {
                snapshot.forEach(doc => {
                    const data = doc.data()
                    asks1.push(data)
                    if(askArr.includes(data.askPrice)){
                        askAmtObj[data.askPrice]++
                    }
                    else{
                        askArr.push(data.askPrice)
                        askAmtObj[data.askPrice] = 1
                    }
                })
                askArr.sort(function(a, b) {
                    return a - b;
                });
                setAsks(asks1)
                setAskPrices(askArr)
                setAskAmt(askAmtObj)
            })
        }
    }
    useEffect(() => {
        getAsks()
    }, [show])

    return(
        <div className="OpenAsks">
            <div className="holdings-offers-div"><Link onClick={()=>{setShow(!show)}}>{show ? 'Close' : 'Open'} Offers to Sell</Link></div>
        </div>
    )
}

export default OpenAsks
