import React, {useState, useEffect} from 'react'
import './tradeblock.css'
import {db} from '../../services/firebase'

const TBTE_Offers = (props) => {
    //props
    const t = props.t

    //states
    const [bids, setBids] = useState([])
    const [bidAmt, setBidAmt] = useState({})
    const [asks, setAsks] = useState([])
    const [askAmt, setAskAmt] = useState({})

    //Functions
    const getOffers = () => {
        let bidArr = []
        let bidAmtObj = {}
        let askArr = []
        let askAmtObj = {}
        db.collection('Asks').where('team', '==', t.team).onSnapshot(snapshot => {
            snapshot.forEach(doc => {
                const data = doc.data()
                if(askArr.includes(data.askPrice)){
                    let num = askAmtObj[data.askPrice]
                    askAmtObj[data.askPrice]++
                    console.log('here')
                }
                else{
                    askArr.push(data)
                    askAmtObj[data.askPrice] = 1
                }
            })
            console.log(askArr)
            console.log(askAmtObj)
        })
        db.collection('Bids').where('bidTeam', '==', t.team).onSnapshot(snapshot => {
            snapshot.forEach(doc => {
                const data = doc.data()
                bidArr.push(data)
            })
        })
    }
    useEffect(() => {
        getOffers()
    }, [])

    return(
        <div className="tbte-offers-div">
            <div className="tbte-offers-div-sub">
                <div className="tbte-offers-name">Offers To Buy</div>
                <div className="tbte-offers-item">2 x $31.00</div>
                <div className="tbte-offers-item">4 x $30.50</div>
                <div className="tbte-offers-item">1 x $28.92</div>
                <div className="tbte-offers-item">5 x $26.00</div>
            </div>
            <div className="tbte-offers-div-sub">
                <div className="tbte-offers-name">Offers To Sell</div>
                <div className="tbte-offers-item">7 x $32.33</div>
                <div className="tbte-offers-item">2 x $34.34</div>
                <div className="tbte-offers-item">4 x $35.00</div>
                <div className="tbte-offers-item">3 x $35.60</div>
                <div className="tbte-offers-item">3 x $37.00</div>
            </div>
        </div>
    )
}

export default TBTE_Offers
