import React, {useState, useEffect} from 'react'
import './tradeblock.css'
import TBofferCard from './TBofferCard'
import moment from 'moment'
import {db} from '../../services/firebase'

const TBoffers = (props) => {

    const nba = ['Blazers', 'Bucks', 'Celtics', 'Kings', 'Magic', 'Spurs', 'Warriors']
    const nfl = ['Patriots', 'Raiders', 'Giants', 'Chargers']

    //states
    const [bids, setBids] = useState([])
    const [asks, setAsks] = useState([])
    const [offers, setOffers] = useState([])
    const [sortType, setSortType] = useState('all')

    //Functions
    let date = moment().subtract(18,'d').format('YYYYMMDD')
    let todate = moment().format('YYYYMMDD')
    const getOffers = () => { 
        let bidArr = []
        let askArr = []
        let AD = 0 
        let BD = 0
        let nbaAD = 0
        let nbaAW = 0
        let nbaBD = 0
        let nbaBW = 0
        let nflAD = 0
        let nflAW = 0
        let nflBD = 0
        let nflBW = 0
        db.collection('Bids').where('date', '>=', date).onSnapshot(querySnap => {
            querySnap.forEach(doc => {
                const data = doc.data()
                bidArr.push(data)
                if(nba.includes(data.bidTeam)){
                    nbaBW++
                }else if(nfl.includes(data.bidTeam)){
                    nflBW++
                }
                if(data.date >= todate){
                    BD++
                    if(nba.includes(data.bidTeam)){
                        nbaBD++
                    }else if(nfl.includes(data.bidTeam)){
                        nflBD++
                    }
                }
            })
            setBids(bidArr)
        })
        db.collection('Asks').where('date', '>=', date).onSnapshot(querySnap => {
            querySnap.forEach(doc => {
                const data = doc.data()
                askArr.push(data)
                if(nba.includes(data.team)){
                    nbAW++
                }else if(nfl.includes(data.team)){
                    nflAW++
                }
                if(data.date >= todate){
                    AD++
                    if(nba.includes(data.team)){
                        nbaAD++
                    }else if(nfl.includes(data.team)){
                        nflAD++
                    }
                }
            })
            setAsks(askArr)
        })
    }
    useEffect(() => {
        getOffers()
    }, [])
    const combineOffers = () => {
        let offersArr = bids.concat(asks)
        offersArr.sort(function(a, b) {
            return a.date - b.date;
        });
        setOffers(offersArr)
    }
    useEffect(() => {
        combineOffers()
    }, [bids, asks])
 
    return(
        <div className="TBoffers">
            {props.sort && <><button onClick={()=>{setSortType('nfl')}}>simNFL only</button><button onClick={()=>{setSortType('nba')}}>simNBA only</button><button onClick={()=>{setSortType('all')}}>All</button></>}
            <div className="tb-o-div-left">
                <div className="tb-t-title">Bids</div>
                <div className="tb-t-day">1 Day</div>
                <div className="tb-t-day">7 Days</div>
                <hr className="tb-t-hr" />
                <hr className="tb-t-hr" />
                <div className="tb-t-amt">41</div>
                <div className="tb-t-amt">{bids.length > 0 ? bids.length : '0'}</div>
            </div>
            <div className="tb-o-div-right">
                <div className="tb-t-title">Asks</div>
                <div className="tb-t-day">1 Day</div>
                <div className="tb-t-day">7 Days</div>
                <hr className="tb-t-hr" />
                <hr className="tb-t-hr" />
                <div className="tb-t-amt">41</div>
                <div className="tb-t-amt">{asks.length > 0 ? asks.length : '0'}</div>
            </div>
            <div className='tb-cards'>
                {sortType === 'all' && offers.map(o => {
                    return <TBofferCard teams={props.teams} o={o} />
                })}
                {sortType === 'nfl' && offers.map(o => {
                    return <TBofferCard teams={props.teams} o={o} />
                })}
                {sortType === 'nba' && offers.map(o => {
                    return <TBofferCard teams={props.teams} o={o} />
                })}
            </div>
        </div>
    )
}

export default TBoffers
