import React, {useState, useEffect} from 'react'
import './portfolio.css'

const PTotal = (props) => {
    //get props
    const user = props.user
    const teams = props.teams

    //get states
    const [value, setValue] = useState(false)

    //Functions
    const getValue = () => {
        let holder = 0
        teams && teams.map(t => {
            user.shares.map(s => {
                let team = s.substring(0, s.length - 4)
                if(team = t.team){
                    holder = holder + t.lastSoldPrice
                }
            })
        })
        return setValue(holder + (user.cash && user.cash))
    }
    useEffect(() => {
        return getValue()
    }, [props])

    return(
        <div className='PTotal'>
            <div className="p-total-account">Account Value</div>
            <hr className="p-total-hr" />
            <div className="p-total-value">${value && value.toFixed(2)}</div>
            <div className="p-total-cash">Cash: <span className="p-total-cash-amt">${user.cash && user.cash.toFixed(2)}</span></div>
        </div>
    )
}

export default PTotal
